		int ten = sc.nextInt();
		int ten5 = sc.nextInt();
		int hund = sc.nextInt();
		int hund5 = sc.nextInt();

		int sumMon = ten * 10 + ten5 * 50 + hund * 100 + hund5 * 500;
		int returnMon = sumMon - n;

		int retTen = 0;
		int retTen5= 0;
		int retHund = 0;
		int retHund5 = 0;

		while(returnMon >= 500){
			returnMon = returnMon - 500;
			retHund5++;
		}
		while(returnMon >= 100){
			returnMon = returnMon - 100;
			retHund++;
		}
		while(returnMon >= 50){
			returnMon = returnMon - 50;
			retTen5++;
		}
		while(returnMon >= 10){
			returnMon = returnMon - 10;
			retTen++;
		}

		if(retTen < ten) {
			System.out.print("10 ");
