	int[] VAL = {10, 50, 100, 500};
	int calc(int x) {
		if (x < 0) return Integer.MAX_VALUE;

		int res = 0;
		for (int i = 3; i >= 0; i--) {
			while (x >= VAL[i]) {
				x -= VAL[i];
				res++;
			}
		}
		return res;
	}

	public void run() {
		int turn = 0;
		while (true) {
			int price = in.nextInt();
