public class Main {
  Scanner sc;

  void run() {
    int[] mist = new int[] { 10, 50, 100, 500 };
    for ( int i = 0;; ++i ) {
      int n = ni();
      if ( n == 0 ) {
        break;
      }
      int[] list = new int[4];
      for ( int j = 0; j < 4; ++j ) {
        list[ j ] = ni();
      }
      int[] ans = new int[4];
      int min = 1 << 28;
      int tmin = 1 << 28;
      for ( int a = 0; a <= list[ 0 ]; ++a ) {
        for ( int b = 0; b <= list[ 1 ]; ++b ) {
          for ( int c = 0; c <= list[ 2 ]; ++c ) {
            for ( int d = 0; d <= list[ 3 ]; ++d ) {
              int sum = a * mist[ 0 ] + b * mist[ 1 ] + c * mist[ 2 ] + d
                  * mist[ 3 ];
              if ( sum < n )
                continue;
              int v = list[ 0 ] + list[ 1 ] + list[ 2 ] + list[ 3 ]
                  - ( a + b + c + d );
              int v_ = 0;
              int n_ = sum - n;
              for ( int k = 3; 0 <= k; --k ) {
                int p = n_ / mist[ k ];
                n_ -= mist[ k ] * p;
                v_ += p;
              }
              if ( min < v + v_ )
                continue;
              if ( min == v + v_ ) {
                if ( tmin <= v_ )
                  continue;
                tmin = v_;
                ans[ 0 ] = a;
                ans[ 1 ] = b;
                ans[ 2 ] = c;
                ans[ 3 ] = d;
              } else {
                min = v + v_;
                tmin = v_;
                ans[ 0 ] = a;
                ans[ 1 ] = b;
                ans[ 2 ] = c;
                ans[ 3 ] = d;
              }
              // debug(min, a, b, c, d, sum, v, v_);
            }
          }
        }
      }
      if ( i > 0 ) {
        System.out.println();
      }
      for ( int j = 0; j < 4; ++j ) {
        if ( ans[ j ] == 0 )
          continue;
        System.out.println( mist[ j ] + " " + ans[ j ] );
      }
    }
  }

  Main() {
    sc = new Scanner( System.in );
  }

  int ni() {
    return sc.nextInt();
  }

  public static void main(String[] args) {
    new Main().run();
  }

  void debug(Object... os) {
    System.err.println( Arrays.deepToString( os ) );
  }

}

