class Reader {
    private final InputStream sc;
    private final byte[] buf = new byte[1024];
    private int ptr = 0;
    private int buflen = 0;

    public Reader() { this(System.in);}
    public Reader(InputStream source) { this.sc = source;}

    private boolean hasNextByte() {
        if (ptr < buflen) return true;
        ptr = 0;
        try{
            buflen = sc

.read(buf);
        }catch (IOException e) {e.printStackTrace();}
        if (buflen <= 0) return false;
        return true;
    }

    private int readByte() { if (hasNextByte()) return buf[ptr++]; else return -1;}

    private boolean isPrintableChar(int c) { return 33 <= c && c <= 126;}

    private void skip() { while(hasNextByte() && !isPrintableChar(buf[ptr])) ptr++;}

    public boolean hasNext() {skip(); return hasNextByte();}

    public String next() {
        if (!hasNext()) throw new NoSuchElementException();
        StringBuilder sb = new StringBuilder();
        int b = readByte();
        while (isPrintableChar(b)) {
            sb.appendCodePoint(b);
            b = readByte();
        }
        return sb.toString();
    }

    public long nextLong() {
        if (!hasNext()) throw new NoSuchElementException();
        boolean minus = false;
        long num = readByte();

        if(num == '-'){
            num = 0;
            minus = true;
        }else if (num < '0' || '9' < num){
            throw new NumberFormatException();
        }else{
            num -= '0';
        }

        while(true){
            int b = readByte();
            if('0' <= b && b <= '9')
                num = num * 10 + (b - '0');
            else if(b == -1 || !isPrintableChar(b))
                return minus ? -num : num;
            else
                throw new NoSuchElementException();
        }
    }

    public int nextInt() {
        long num = nextLong();
        if (num < Integer.MIN_VALUE || Integer.MAX_VALUE < num)
            throw new NumberFormatException();
        return (int)num;
    }

    public double nextDouble() {
        return Double.parseDouble(next());
    }

    public char nextChar() {
        if (!hasNext()) throw new NoSuchElementException();
        return (char)readByte();
    }

    public String nextLine() {
        while (hasNextByte() && (buf[ptr] == '\n' || buf[ptr] == '\r')) ptr++;
        if (!hasNextByte()) throw new NoSuchElementException();

        StringBuilder sb = new StringBuilder();
        int b = readByte();
        while (b != '\n' && b != '\r') {
            sb.appendCodePoint(b);
            b = readByte();
        }

        return sb.toString();
    }

    public int[] nextIntArray(int n) {
        int[] res = new int[n];
        for (int i=0; i<n; i++) res[i] = nextInt();
        return res;
    }

    public char[] nextCharArray(int n) {
        char[] res = new char[n];
        for (int i=0; i<n; i++) res[i] = nextChar();
        return res;
    }

    public void close() {try{ sc.close();}catch(IOException e){ e.printStackTrace();}};
}

import java.util.Scanner;

public class Main {

	static Scanner sc = new Scanner(System.in);

	public static void main(String[] arg) {
		int[] c = new int[4];
		int[] best = new int[4];
		int[] coin = { 10, 50, 100, 500 };
		for (int t = 0;; ++t) {
			int P = sc.nextInt();
			if (P == 0) break;
			for (int i = 0; i < 4; ++i) {
				c[i] = sc.nextInt();
			}
			int min = 1000;
			for (int i = 0; i <= c[0]; ++i) {
				for (int j = 0; j <= c[1]; ++j) {
					for (int k = 0; k <= c[2]; ++k) {
						for (int l = 0; l <= c[3]; ++l) {
							int sum = i * 10 + j * 50 + k * 100 + l * 500;
							if (sum < P) continue;
							int rest = sum - P;
							int ret = c[0] + c[1] + c[2] + c[3] - i - j - k - l;
							ret += rest / 500;
							rest -= rest / 500 * 500;
							ret += rest / 100;
							rest -= rest / 100 * 100;
							ret += rest / 50;
							rest -= rest / 50 * 50;
							ret += rest / 10;
							if (ret < min) {
								min = ret;
								best[0] = i;
								best[1] = j;
								best[2] = k;
								best[3] = l;
							}
						}
					}
				}
			}
			if (t > 0) System.out.println();
			for (int i = 0; i < 4; ++i) {
				if (best[i] != 0) {
					System.out.println(coin[i] + " " + best[i]);
				}
			}
		}
	}
}

