public class Main {

	public static int calc(int n, int ten, int fifty, int hund, int fihund){
		int rest = (ten * 10 + fifty * 50 + hund * 100 + fihund * 500) - n;

		int r_ten = 0;
		int r_fifty = 0;
		int r_hund = 0;
		int r_fihund = 0;

		r_fihund = (rest / 500);
		rest -= r_fihund * 500;
		r_hund = (rest / 100);
		rest -= r_hund * 100;
		r_fifty = (rest / 50);
		rest -= r_fifty * 50;
		r_ten = (rest / 10);
		rest -= r_ten * 10;

		return r_ten + r_fifty + r_hund + r_fihund;
	}

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);
		boolean first = true;

		while(true){
			final int n = sc.nextInt();

			if(n == 0){
				break;
			}

			if(!first){
				System.out.println();
			}else{
				first = false;
			}

			final int ten = sc.nextInt();
			final int fifty = sc.nextInt();
			final int hund = sc.nextInt();
			final int fihund = sc.nextInt();

			int min = Integer.MAX_VALUE;
			int[] min_count = new int[4];
			for(int i = 0; i <= fihund; i++){
				if(n <= 500 * i){
					int sum = calc(n, 0, 0, 0, i);

					if(min > sum){
						min = sum;
						min_count[0] = i;
						min_count[1] = 0;
						min_count[2] = 0;
						min_count[3] = 0;
					}
				}

				for(int j = 0; j <= hund; j++){
					if(n <= 500 * i + 100 * j){
						int sum = calc(n, 0, 0, j, i);

						if(min > sum){
							min = sum;
							min_count[0] = i;
							min_count[1] = j;
							min_count[2] = 0;
							min_count[3] = 0;
						}
					}

					for(int k = 0; k <= fifty; k++){
						if(n <= 500 * i + 100 * j + 50 * k){
							int sum = calc(n, 0, k, j, i);

							if(min > sum){
								min = sum;
								min_count[0] = i;
								min_count[1] = j;
								min_count[2] = k;
								min_count[3] = 0;
							}
						}

						for(int l = 0; l <= ten; l++){
							if(n <= 500 * i + 100 * j + 50 * k + 10 * l){
								int sum = calc(n, l, k, j, i);

								if(min > sum){
									min = sum;
									min_count[0] = i;
									min_count[1] = j;
									min_count[2] = k;
									min_count[3] = l;
								}
							}
						}
					}
				}
			}

			int[] size = new int[]{500, 100, 50, 10};
			for(int i = 3; i >= 0; i--){
				if(min_count[i] > 0){
					System.out.println(size[i] + " " + min_count[i]);
				}
			}
		}
	}

}

