			}

		}
		int solve2(int[] s){
			int sum=0;
			for(int i=0; i<4; ++i)sum+=s[i]*d[i];
			int diff=sum-n;
//			System.out.println("sum"+sum+" diff"+diff);
			if(diff<0)	return -1;
			int[] t=new int[4];
			for(int i=3; i>=0; --i){
				while(diff>=d[i]){
					diff-=d[i];
					++t[i];
				}
			}

			int ret=0;
			for(int i=0; i<4; ++i){
				if(s[i]>0 && t[i]>0)	return -1;
				ret+=t[i];
			}
//			System.out.println("R"+Arrays.toString(t));
			return ret;
		}
	}

