      int otCan = (price/10)/5;
      otCan = Math.min(ot/5,otCan);
      price -= 50*otCan;
      ot -= 5*otCan;
      cot += 5*otCan;
//System.out.println("  "+price);

      int ftRest = (price%100)/50;
      if(ftRest <= ft) {
        price -= 50*ftRest;
        ft -= ftRest;
        cft += ftRest;
      }
//System.out.println("  "+price);
      int ftCan = (price/50)/2;
      ftCan = Math.min(ft/2,ftCan);
      price -= 100*ftCan;
      ft -= 2*ftCan;
      cft += 2*ftCan;
//System.out.println("  "+price);
      if(price%50>0) {
        ftCan = Math.min(ft,1);
        price -= 50*ftCan;
        ft -= 1*ftCan;
        cft += 1*ftCan;
      }

      int ohRest = (price%500)/100;
      if(ohRest <= oh) {
        price -= 100*ohRest;
        oh -= ohRest;
        coh += ohRest;
      }
