      int ohCan = (price/100)/5;
      ohCan = Math.min(oh/5,ohCan);
      price -= 500*ohCan;
      oh -= 5*ohCan;
      coh += 5*ohCan;
      ohCan = oh/5;
      price -= 500*ohCan;
      oh -= 5*ohCan;
      coh += 5*ohCan;
      if((price/50)%2==1 && oh > 0) {
        price -= 100;
        oh --;
        coh ++;
      }
//System.out.println("  "+price);
      if(price%500>0) {
        ohCan = (price%500)/100+1;
        ohCan = Math.min(oh,ohCan);
        price -= 100*ohCan;
        oh -= 1*ohCan;
        coh += 1*ohCan;
      }

      if(price > 0) {
        int fhCan = price/500;
        if(price%500 > 0) fhCan++;
        price -= 500*fhCan;
        fh -= fhCan;
        cfh += fhCan;
      }
//System.out.println("  "+price);

