        int fhCan = price/500;
        price -= 500*fhCan;
        fh += fhCan;
        cfh -= fhCan;
  //System.out.println("  "+price);

        ohCan = price/100;
        price -= 100*ohCan;
        oh += ohCan;
        coh -= ohCan;
  //System.out.println("  "+price);

        ftCan = price/50;
        price -= 50*ftCan;
        ft += ftCan;
        cft -= ftCan;
  //System.out.println("  "+price);

