	public void calc2(int n){
		int ten = sc.nextInt();
		int ten5 = sc.nextInt();
		int hund = sc.nextInt();
		int hund5 = sc.nextInt();

		int hantei = 0;
		for(int i100 = 0; i100 < 5; i100++){
			for(int i10 = 0; i10 < 5; i10++){
				if(kanou(n + i100 + i10, ten,ten5,hund,hund5, 0,0,0,0)) {
					hantei = 1;
					break;
				}
				else if(kanou(n + i100 + i10 + 50, ten,ten5,hund,hund5, 0,0,0,0)) {
					hantei = 1;
					break;
				}
			}
			if(hantei == 1) break;
		}
	}

	public boolean kanou(int n, int ten, int ten5, int hund, int hund5, int Oten, int Oten5, int Ohund, int Ohund5){
		if(n == 0) {
			if(Oten != 0) System.out.println("10 " + Oten);
			if(Oten5 != 0) System.out.println("50 " + Oten5);
			if(Ohund != 0) System.out.println("100 " + Ohund);
			if(Ohund5 != 0) System.out.println("500 " + Ohund5);
			return true;
		}
		else if(n >= 500 && hund5 > 0) return kanou(n-500, ten, ten5, hund, hund5 - 1, Oten, Oten5, Ohund, Ohund5+1);
		else if(n >= 100 && hund > 0) return kanou(n-100, ten, ten5, hund - 1, hund5, Oten, Oten5, Ohund+1, Ohund5);
		else if(n >= 50 && ten5 > 0) return kanou(n-50, ten, ten5 - 1, hund, hund5, Oten, Oten5+1, Ohund, Ohund5);
		else if(n >= 10 && ten > 0) return kanou(n-10, ten-1, ten5, hund, hund5, Oten+1, Oten5, Ohund, Ohund5);
		else return false;
	}
	public static void main(String[] args) {
		new Main().run();
	}
}

import java.util.*;

class Main{

    static int[] kouka = {10,50,100,500};

    public static void main(String[] args){
	Scanner sc = new Scanner (System.in);

	int money = sc.nextInt();

	boolean boo = false;
	while(money!=0){
	    if(boo)System.out.println();
	    boo = true;
	    int[] coin = new int[4];
	    for(int i=0; i<coin.length; i++){
		coin[i] = sc.nextInt();
	    }

	    String ans = solve(coin,money);
	    System.out.print(ans);

	    money = sc.nextInt();
	}
    }

    public static String solve(int[] coin, int money){
	int sum = 0;
	for(int i=0; i<coin.length; i++){
	    sum += kouka[i] * coin[i];
	}
	int[] oturiKouka = getOturi(sum,money);
	String ans = "";
	for(int i=0; i<coin.length; i++){
	    if(coin[i]-oturiKouka[i]>0)ans += kouka[i] + " " + (coin[i]-oturiKouka[i]) + "\n";
	}
	return ans;
    }

    public static int[] getOturi(int shiharai,int money){
	int oturi = shiharai - money;
	int[] oturiKouka = new int[4];
	while(oturi!=0){
	    for(int i=kouka.length-1; i>=0;){
		if(oturi-kouka[i]>=0){
		    oturi = oturi - kouka[i];
		    oturiKouka[i]++;
		}else i--;
	    }
	}
	return oturiKouka;
    }
}

import java.util.Arrays;
import java.util.Scanner;

public class Main {
	MyScanner sc = new MyScanner();
	Scanner sc2 = new Scanner(System.in);
	final int MOD = 1000000007;
	int[] dx = { 1, 0, 0, -1 };
	int[] dy = { 0, 1, -1, 0 };

	int[] coin;
	int[] use;
	int[] ans;
	int[] m = { 10, 50, 100, 500 };
	int money;
	int min;

	void run() {
		boolean f = false;
		for (;;) {
			money = sc.nextInt();
			if (money == 0) {
				return;
			}
			if (f) {
				System.out.println();
			}
			f = true;
			coin = sc.nextIntArray(4);
			use = new int[4];
			ans = new int[4];
			min = Integer.MAX_VALUE;
			dfs(0, 0);
			for (int i = 0; i < 4; i++) {
				if (ans[i] != 0) {
					System.out.println(m[i] + " " + ans[i]);
				}
			}
		}
	}

	void dfs(int index, int sum) {
		if (index == 4) {
			int res = use[0] * 10 + use[1] * 50 + use[2] * 100 + use[3] * 500;
			if (res >= money) {
				int sub = res - money;
				int cnt = 0;
				for (int i = 3; i >= 0; i--) {
					cnt += coin[i] - use[i];
					int cc = sub / m[i];
					if (cc != 0) {
						sub -= m[i] * cc;
						cnt += cc;
					}
				}
				if (min > cnt) {
					min = cnt;
					for (int i = 0; i < 4; i++) {
						ans[i] = use[i];
					}
				}
			}
			return;
		}
		for (int i = 0; i <= coin[index]; i++) {
			use[index] = i;
			dfs(index + 1, sum + m[index] * i);
		}
	}

	public static void main(String[] args) {
		new Main().run();
	}

	void debug(Object... o) {
		System.out.println(Arrays.deepToString(o));
	}

