import java.util.*;
import java.io.*;
import java.awt.geom.*;
import java.math.*;

public class Main {

	static final Scanner in = new Scanner(System.in);
	static final PrintWriter out = new PrintWriter(System.out,false);
	static boolean debug = false;
	static boolean f = true;
	static int[] v = {10, 50, 100, 500};

	static boolean solve() {
		int n = in.nextInt();
		if (n == 0) return false;

		int[] c = new int[4];
		int all = 0;
		for (int i=0; i<4; i++) {
			c[i] = in.nextInt();
			all += v[i]*c[i];
		}

		int r = all - n;
		int[] cnt = new int[4];
		for (int i=3; i>=0; i--) {
			cnt[i] = r/v[i];
			r = r%v[i];
		}


		if (!f) out.println();
		for (int i=0; i<4; i++) {
			if (c[i] - cnt[i] > 0) {
				out.println(v[i]+" "+(c[i] - cnt[i]));
			}
		}

		f = false;
		return true;
	}

	public static void main(String[] args) {
		debug = args.length > 0;
		long start = System.nanoTime();

		while(solve());
		out.flush();

		long end = System.nanoTime();
		dump((end - start) / 1000000 + " ms");
		in.close();
		out.close();
	}

	static void dump(Object... o) { if (debug) System.err.println(Arrays.deepToString(o)); }
}

import java.util.Scanner;

public class Main {

    static int[] cache;
    static int[] ret;
    static int[] cacheType = {10, 50, 100, 500};

    static int[] change(int c) {
        int rest[] = new int[4];
        int amount = c;
        for (int i = 3; i >= 0; i--) {
            rest[i] = amount / cacheType[i];
            amount -= rest[i] * cacheType[i];
        }

        return rest;
    }

    static void printRet(int[] rest) {
        for(int i = 0; i < 4; i++) {
            if (rest[i] != 0) {
                System.out.println(cacheType[i] + " " + rest[i]);
            }
        }
    }

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        while (true) {
            int pay = Integer.parseInt(sc.next());
            int sum = 0;
            if (pay == 0)
                break;
            int[] ans = new int[4];

            cache = new int[4];
            for (int i = 0; i < 4; i++) {
                cache[i] = Integer.parseInt(sc.next());
                sum += cache[i] * cacheType[i];
            }
            int c = sum - pay;
//            System.out.println("rest of money: " + c);
            ret = change(c);
//            System.out.println("rest of coins: " + Arrays.toString(ret));

            for (int i =0; i < 4; i++) {
                if (cache[i] >= ret[i])
                    ans[i] = cache[i] - ret[i];
            }

            printRet(ans);
            System.out.println();
        }
    }
}

