				break;
			}
			String[] line2s = reader.readLine().split(" ");
			int counts[]=new int[4];
			for(int i=0;i<4;i++){
				counts[i]=Integer.parseInt(line2s[i]);
			}
			result(sb,buy,counts[0],counts[1],counts[2],counts[3]);
		}
		System.out.print(sb.toString());

	}
}

