			n = in.nextInt();
			if(n==0)return;
			money = new int[4];
			for(int i=0;i<4;i++)money[i] = in.nextInt();
			solve();
			while(in.hasNext()){
				n = in.nextInt();
				if(n==0)break;
				System.out.println();
				money = new int[4];
				for(int i=0;i<4;i++)money[i] = in.nextInt();
