      int min = INF;
      int[] ans = null;
      for (int i = 0; i <= coins[0]; ++i) {
        for (int j = 0; j <= coins[1]; ++j) {
          for (int k = 0; k <= coins[2]; ++k) {
            for (int l = 0; l <= coins[3]; ++l) {
              int v = 10 * i + 50 * j + 100 * k + 500 * l;
              if (y > v) {
                continue;
              }
              int[] sub = new int[]{i, j, k, l};
              int w = v - y;
              int cnt = 0;
              boolean ok = true;
              for (int a = 3; 0 <= a; --a) {
                int m = w / VALUES[a];
                cnt += m;
                ok &= m == 0 || sub[a] == 0;
                w -= m * VALUES[a];
              }
              if (!ok) {
                continue;
              }
              if (cnt < min) {
                min = cnt;
                ans = sub;
              }
            }
          }
        }
      }
      for (int i = 0; i < 4; ++i) {
        if (ans[i] == 0) {
          continue;
        }
        System.out.println(VALUES[i] + " " + ans[i]);
      }
