    			else if(i >= 100)base[i] = Math.min(base[i],base[i-100]+1);
    			else if(i >= 50)base[i] = Math.min(base[i],base[i-50]+1);
    			else base[i] = Math.min(base[i],base[i-10]+1);
    		}
