    {
        this.jyuen = i;
        this.gojyuen = j;
        this.hyakuen = k;
        this.gohyakuen = l;
    }

    public static Coin IntToCoin(int i)
    {
        Coin coin = new Coin(0, 0, 0, 0);
        if (i >= 500)
        {
            coin.gohyakuen = i / 500;
            i = i - (500 * (i / 500));
        }
        if (i >= 100)
        {
            coin.hyakuen = i / 100;
            i = i - (100 * (i / 100));
        }
        if (i >= 50)
        {
            coin.gojyuen = i / 50;
            i = i - (50 * (i / 50));
        }
        coin.jyuen = i / 10;

        return coin;

    }
