template lazyCartesianProduct(R){
	auto lazyCartesianProduct(R[][] rs ...){ return lazycartesianproduct!(R)(rs);}
	struct lazycartesianproduct(R){
		private R[][] rs; 
		private int[] ls;
		int iter=0 ,last=1;
		this (R[][] rs){
			this.rs = rs;
			foreach(r;rs) { ls ~= r.length; last *= r.length;}
		}
		bool empty() const { return iter == last; }
		void popFront() {iter ++; }
		void popBack() { last --; }
		R[] front() {return get(iter);}
		R[] back() {return get(last);}
		R[] get(int n){
			R[] res; 
			for(int i; i < ls.length;i ++){
				res ~= rs[i][n % ls[i]];
				n = n / ls[i];
			}
			return res;
		}
	}
}
