int W, H;

const int MAX_H = 50;
const int MAX_W = 50;
bool isblack[MAX_H][MAX_W];
bool iswhite[MAX_H][MAX_W];
char field[MAX_H][MAX_W];

const int dx[4] = { 1, 0, -1, 0 };
const int dy[4] = { 0, 1, 0, -1 };

void get(int x, int y, bool black) {
    if(!(0 <= x && x < W && 0 <= y && y < H)) return;
    if(field[y][x] != '.') return;

    if(black) {
        if(isblack[y][x]) return;
        isblack[y][x] = true;
    }
    else {
        if(iswhite[y][x]) return;
        iswhite[y][x] = true;
    }

    for(int i = 0; i < 4; i++) {
        get(x + dx[i], y + dy[i], black);
    }
}

int main() {
    for(; scanf("%d%d", &W, &H), W || H; ) {
        memset(isblack, 0, sizeof(isblack));
        memset(iswhite, 0, sizeof(iswhite));

        for(int i = 0; i < H; i++) scanf(" %s", field[i]);


        for(int y = 0; y < H; y++) {
            for(int x = 0; x < W; x++) {
                if(field[y][x] == 'B') {
                    for(int i = 0; i < 4; i++) {
                        get(x + dx[i], y + dy[i], true);
                    }
                }

                if(field[y][x] == 'W') {
                    for(int i = 0; i < 4; i++) {
                        get(x + dx[i], y + dy[i], false);
                    }
                }
            }
        }

        int b = 0;
        int w = 0;
        for(int y = 0; y < H; y++) {
            for(int x = 0; x < W; x++) {
                if(isblack[y][x] && !iswhite[y][x]) b++;
                if(!isblack[y][x] && iswhite[y][x]) w++;
            }
        }

        printf("%d %d\n", b, w);
    }
}

