void dfs(int y, int x, char c, char target) {
  stack<P> st;
  st.push(P(y, x));
  while (!st.empty()) {
    P top = st.top(); st.pop();
    int y = top.first, x = top.second;
    if (outof_range(y, x) || field[y][x] != target) continue;
    field[y][x] = c;
    REP(i, 4) {
