typedef long long ll;
using namespace std;
using namespace boost;
bool on_board(int y, int x, int h, int w) {
    return 0 <= y and y < h and 0 <= x and x < w;
}
int dx[] = {  0, 0, 1, -1 };
int dy[] = { -1, 1, 0,  0 };
int grouping(int y, int x, int k, vector<vector<int> > & group, vector<vector<char> > const & a) {
    assert (a[y][x] == '.' and not group[y][x]);
    int h = a.size();
    int w = a.front().size();
    group[y][x] = k;
    int s = 1;
    for (int i : irange(0,4)) {
        int ny = y + dy[i];
        int nx = x + dx[i];
        if (not on_board(ny, nx, h, w)) continue;
        if (a[ny][nx] == '.' and not group[ny][nx]) {
            s += grouping(ny, nx, k, group, a);
        }
    }
    return s;
}
int main() {
    while (true) {
        int w, h; cin >> w >> h;
        if (w == 0 and h == 0) break;
        vector<vector<char> > a(h, vector<char>(w));
        for (int y : irange(0,h)) {
            for (int x : irange(0,w)) {
                cin >> a[y][x];
            }
        }
        vector<vector<int> > group(h, vector<int>(w));
        int group_num = 0;
        vector<int> group_size;
        group_size.push_back(-1); // dummy, to make 1-origin
        for (int y : irange(0,h)) {
            for (int x : irange(0,w)) {
                if (a[y][x] == '.' and not group[y][x]) {
                    group_num += 1;
                    int s = grouping(y, x, group_num, group, a);
                    group_size.push_back(s);
                }
            }
        }
        vector<pair<bool,bool> > connected(group_num + 1); // 0 is dummy, to make 1-origin
        for (int y : irange(0,h)) {
            for (int x : irange(0,w)) {
                int k = group[y][x];
                if (k) {
                    for (int i : irange(0,4)) {
                        int ny = y + dy[i];
                        int nx = x + dx[i];
                        if (not on_board(ny, nx, h, w)) continue;
                        if (a[ny][nx] == 'B') connected[k].first  = true;
                        if (a[ny][nx] == 'W') connected[k].second = true;
                    }
                }
            }
        }
        int black = 0, white = 0;
        for (int k : irange(1,group_num+1)) { // 1-origin
            if (connected[k].first and not connected[k].second) {
                black += group_size[k];
            } else if (not connected[k].first and connected[k].second) {
                white += group_size[k];
            }
        }
        cout << black << " " << white << endl;
    }
    return 0;
}

#include <iostream>
#include <string>
#include <string.h>

#define W_MAX 52
#define H_MAX 52

int f[H_MAX][W_MAX];
int dx[4]={1,0,-1,0};
int dy[4]={0,1,0,-1};

void dfs1(int x,int y);
void dfs2(int x,int y);

using namespace std;

int main(void)
{
	int w,h;
	char c;
	int i1,i2;
	int count1,count2;

	while(1){
		cin >> w >> h;
		if(w+h==0) break;

		memset(f,-1,sizeof(f));

		for(i1=1;i1<=h;i1++){
			for(i2=1;i2<=w;i2++){
				cin >> c;
				if(c=='.') f[i1][i2]=0;
				else if(c=='B') f[i1][i2]=1;
				else if(c=='W') f[i1][i2]=2;
			}
		}

		for(i1=1;i1<=h;i1++){
			for(i2=1;i2<=w;i2++){
				if(f[i1][i2]==1) {
					f[i1][i2]=-1;
					dfs1(i2,i1);
				}
				else if(f[i1][i2]==2){
					f[i1][i2]=-1;
					dfs2(i2,i1);
				}
			}
		}

		count1=0;
		count2=0;
		for(i1=1;i1<=h;i1++){
			for(i2=1;i2<=w;i2++){
				if(f[i1][i2]==3) count1++;
				else if(f[i1][i2]==4) count2++;
			}
		}
		cout << count1 << " " << count2 << endl;
	}

	return 0;


}

void dfs1(int x,int y)
{
	int i1;
	int xx,yy;

	for(i1=0;i1<4;i1++){
		xx=x+dx[i1];
		yy=y+dy[i1];

		if(f[yy][xx]==0 || f[yy][xx]==4){
			f[yy][xx]+=3;
			dfs1(xx,yy);
		}
	}
}

void dfs2(int x,int y)
{
	int i1;
	int xx,yy;

	for(i1=0;i1<4;i1++){
		xx=x+dx[i1];
		yy=y+dy[i1];

		if(f[yy][xx]==0 || f[yy][xx]==3){
			f[yy][xx]+=4;
			dfs2(xx,yy);
		}
	}
}

#include<iostream>
#include<cstring>
#include<algorithm>
using namespace std;

int dy[]={0,1,0,-1};
int dx[]={1,0,-1,0};
char m[50][51];
int w,h;

bool visit[50][50];
int flag;
int dfs(int y,int x){
	if(y<0||h<=y||x<0||w<=x || visit[y][x])return 0;
	if(m[y][x]=='B'){
		flag|=1;
		return 0;
	}
	if(m[y][x]=='W'){
		flag|=2;
		return 0;
	}
	visit[y][x]=true;
	int res=1;
	for(int i=0;i<4;i++){
		res += dfs(y+dy[i],x+dx[i]);
	}
	return res;
}
int main(){
	while(cin>>w>>h,w|h){
		int black=0,white=0;
		for(int i=0;i<h;i++)cin>>m[i];
		memset(visit,false,sizeof(visit));
		for(int y=0;y<h;y++){
			for(int x=0;x<w;x++){
				if(visit[y][x]||m[y][x]!='.')continue;
				flag=0;
				int s=dfs(y,x);
				if(flag==1)black+=s;
				else if(flag==2)white+=s;
			}
		}
		cout<<black<<' '<<white<<endl;
	}
	return 0;
}

