const int dx[] = {-1,0,1,0}, dy[] = {0,1,0,-1};

int W, H;

pair<bool, int> dfs_count(vvc& field, int x, int y, char c) {
    field[y][x] = c;
    int cnt = 0;
    REP(i, 4) {
        int sx = x + dx[i], sy = y + dy[i];
        if (0 <= sx && sx < W && 0 <= sy && sy < H) {
            if (field[sy][sx] == '.') {
                auto retval = dfs_count(field, sx, sy, c);
                if (!retval.first) {
                    return {false, 0};
                }
                cnt += retval.second + 1;
            } else if (field[sy][sx] != c) {
                return {false, 0};
            }
        }
    }
    return {true, cnt};
}

int main() {
    while (cin >> W >> H, W|H) {
        vvc field(H, vc(W));
        REP(y, H) REP(x, W) cin >> field[y][x];
        int b = 0, w = 0;
        REP(y, H) REP(x, W) {
            if (field[y][x] == '.') continue;
            REP(i, 4) {
                vvc copy = field;
                int sx = x + dx[i], sy = y + dy[i];
                if (0 <= sx && sx < W && 0 <= sy && sy < H && field[sy][sx] == '.') {
                    if (field[y][x] == 'B') {
                        auto retval = dfs_count(field, sx, sy, 'B');
                        if (retval.first) {
                            b += retval.second + 1;
                        } else {
                            field = copy;
                        }
                    } else if (field[y][x] == 'W') {
                        auto retval = dfs_count(field, sx, sy, 'W');
                        if (retval.first) {
                            w += retval.second + 1;
                        } else {
                            field = copy;
                        }
                    }
                }
            }
        }
//        REP(y, H) { REP(x, W) { LOG("%c", field[y][x]); } LOG("\n"); }

        cout << b << " " << w << endl;
    }
}

