int MW, MH;
vector<string> a;
vector<vector<bool>> used;
bool inside(int nh, int nw) {
    return 0 <= nw && nw < MW && 0 <= nh && nh < MH;
}
int dh[] = {-1, 0, 0, 1};
int dw[] = {0, -1, 1, 0};
bool white, black;
int dfs(PII now, PII back) {
    int w, h; tie(h, w) = now;
    if (a[h][w] == 'W') {
        white = true;
        return 0;
    }
    if (a[h][w] == 'B') {
        black = true;
        return 0;
    }
    int res = 0;
    REP(i, 4) {
        int nh = h + dh[i], nw = w + dw[i];
        if (!inside(nh, nw) || used[nh][nw]) continue;
        if (back == MP(nh, nw)) continue;
        if (a[nh][nw] == '.') used[nh][nw] = true;
        res += dfs(MP(nh, nw), now);
    }
    return res + 1;
}

void solve(int W, int H) {
    MW = W;MH = H;
    a.resize(H);used.resize(H, vector<bool>(W, false));
    for_each(ALL(a), [](string &e){cin >> e;});
    PII ans = MP(0, 0);
    REP(i, H)REP(j, W) if (!used[i][j] && a[i][j] == '.') {
        used[i][j] = true;
        white = false;
        black = false;
        int buf = dfs(MP(i, j), MP(-1, -1));
        //DEBUG(buf)
        if (white == black) {
            buf = 0;
        }
        if (black && !white) ans.first += buf;
        else if (!black && white) ans.second += buf;
    }
    cout << ans.first << " " << ans.second << endl;
    a.clear();used.clear();
    return;
}

