int dy[4] = {0, 1, 0, -1};

bool iswhite(char c)
{
    return (c == 'W' || c == 'w');
}

bool isblack(char c)
{
    return (c == 'B' || c == 'b');
}

void expand(char a[51][51])
{
    bool ischanged = false;
    for(int i = 0; i < h; i++){
        for(int j = 0; j < w; j++){
            if(a[i][j] == '.') continue;
            for(int k = 0; k < 4; k++){
                int nx = i + dx[k], ny = j + dy[k];
                if(nx < 0 || nx >= h || ny < 0 || ny >= w || a[nx][ny] == 'B' || a[nx][ny] == 'W') continue;
                if(iswhite(a[i][j])){
                    if(a[nx][ny] == '.') a[nx][ny] = 'w';
                    else if(isblack(a[nx][ny])) a[nx][ny] = 'X';
                    else continue;
                } else if(isblack(a[i][j])){
                    if(a[nx][ny] == '.') a[nx][ny] = 'b';
                    else if(iswhite(a[nx][ny])) a[nx][ny] = 'X';
                    else continue;
                } else {
                    if(a[nx][ny] == '.' || a[nx][ny] == 'w' || a[nx][ny] == 'b') a[nx][ny] = 'X';
                    else continue;
                }
                ischanged = true;
            }
        }
    }
    if(ischanged) expand(a);
    else return;
}

void solve(char a[51][51])
{
    expand(a);
    int wnum = 0, bnum = 0;
    for(int i = 0; i < h; i++){
        for(int j = 0; j < w; j++){
            if(a[i][j] == 'w') wnum++;
            else if(a[i][j] == 'b') bnum++;
            // cout << a[i][j];
        }
        // cout << endl;
    }
    cout << bnum << " " << wnum << endl;
    return;
}

int main()
{
    while(1){
        cin >> w >> h;
        if(w == 0 && h == 0) break;
        char a[51][51];
        for(int i = 0; i < h; i++){
            for(int j = 0; j < w; j++){
                cin >> a[i][j];
            }
        }
        solve(a);
    }
    return 0;
}

