#define W 100
#define B 200

using namespace std;

class PointQueue{
	public:
		queue<int> x;
		queue<int> y;

		void push(int px,int py);
		int frontX();
		int frontY();
		void pop();
		int size();
};

void PointQueue::push(int px,int py){
	x.push(px);
	y.push(py);
}

int PointQueue::frontX(){
	return x.front();
}
int PointQueue::frontY(){
	return y.front();
}

void PointQueue::pop(){
	x.pop();
	y.pop();
}

int PointQueue::size(){
	return x.size();
}

int max(int a,int b){
	if(a>=b)return a;
	else return b;
}

int data[52][52];
int flag[52][52];



int main(){

	PointQueue q;

	int w,h;
	while(cin>>w>>h){
		if(w==0&&h==0)break;

	int dx[4]={0,1,0,-1};
	int dy[4]={1,0,-1,0};
	int ansB=0,ansW=0;

	for(int i=0;i<=h+1;i++){
		for(int j=0;j<=w+1;j++){
			char tmp;

			if(i==0 || i==h+1 || j==0 || j==w+1){
				data[i][j]=-1;
				flag[i][j]=1;
			}
			else{
				cin>>tmp;
				if(tmp=='.'){
					flag[i][j]=0;
					data[i][j]=0;
				}
				else if(tmp=='W'){
					data[i][j]=W;
					flag[i][j]=1;
				}
				else if(tmp=='B'){
					data[i][j]=B;
					flag[i][j]=1;
				}
			}
		}
	}

	for(int i=1;i<=h;i++){
		for(int j=1;j<=w;j++){
			int state=0;
			int space=0;
			if(flag[i][j]==0){
				q.push(j,i);
				flag[i][j]=1;
				while(q.size()!=0){
					space++;
					int topX=q.frontX();
					int topY=q.frontY();
					q.pop();

					for(int k=0;k<4;k++){
						int nextX=dx[k]+topX;
						int nextY=dy[k]+topY;

						if(flag[nextY][nextX]==0){
							q.push(nextX,nextY);
							flag[nextY][nextX]=1;
						}
						else if(data[nextY][nextX]==W){
							if(state==B){
								space=-10000000;
							}
							else{
								state=W;
							}
						}
						else if(data[nextY][nextX]==B){
							if(state==W){
								space=-10000000;
							}
							else{
								state=B;
							}
						}
					}
				}
				if(state==W) ansW += max(0,space);
				else if(state==B) ansB += max(0,space);
			}
		}
	}

	cout<<ansB<<" "<<ansW<<endl;
	}

	return 0;
}

