int W, H;
const int HMAX = 58, WMAX = 58;
char field[HMAX][WMAX];
const int dx[4] = {1, 0, -1, 0};
const int dy[4] = {0, 1, 0, -1};

bool outof_range(int h, int w) {
  return h < 0 || W <= h || w < 0 || W <= w;
}

void dfs(int h, int w, char c, char target) {
  if (outof_range(h, w) or field[h][w] != target) {
    return;
  }
  field[h][w] = c;
  REP(dir, 4) {
    dfs(h + dy[dir], w + dx[dir], c, target);
  }
}

int main() {
  cout << fixed << setprecision(10);
  while (cin >> W >> H and W) {
    REP(y, HMAX) REP(x, WMAX) {
      field[y][x] = '#';
    }
    REP(y, H) {
      string row; cin >> row;
      REP(x, W) {
        field[y][x] = row[x];
      }
    }
    REP(y, H) REP(x, W) {
      if (field[y][x] == 'W') {
        REP(i, 4) {
          dfs(y + dy[i], x + dx[i], 'w', '.');
        }
      }
      if (field[y][x] == 'B') {
        REP(i, 4) {
          dfs(y + dy[i], x + dx[i], 'b', '.');
        }
      }
    }
    REP(y, H) REP(x, W) {
      if (field[y][x] == 'W') {
        REP(i, 4) {
          dfs(y + dy[i], x + dx[i], 'x', 'b');
        }
      }
      if (field[y][x] == 'B') {
        REP(i, 4) {
          dfs(y + dy[i], x + dx[i], 'x', 'w');
        }
      }
      if (field[y][x] == 'x') {
        REP(i, 4) {
          dfs(y + dy[i], x + dx[i], 'x', 'w');
          dfs(y + dy[i], x + dx[i], 'x', 'b');
        }
      }
    }
    int white = 0, black = 0;
    REP(y, H) REP(x, W) {
      char c = field[y][x];
      if (c == 'w') { white++; }
      if (c == 'b') { black++; }
    }
    cout << black << " " << white << endl;
  }
  return 0;
}

