void adja(int pos,int color,int w,int h){
  int x=pos%w;
  int y=pos/w;
  for (int dir=0;dir<4;dir++){
    int npos = pos + dx[dir]+dy[dir]*w;
    int nx = x + dx[dir];
    int ny = y + dy[dir];
    if (ny>=0 && ny <= h-1 && nx>=0 && nx<= w-1){
      if (map[npos]==0){
        if ((adj[npos]>>(color-1))%2==0) {
          adj[npos]+=color;
          adja(npos,color,w,h);
        }
      }
    }
  }

}

