int main(){
	int w, h;
	int dw[] = {1,0,-1,0};
	int dh[] = {0,1,0,-1};
	while(cin >> w >> h, w+h){
		vector<string> x(h+2);
		for(int i = 1; i <= h; ++i){
			cin >> x[i];
			x[i] = "#" + x[i] + "#";
		}
		for(int i = 0; i < w+2; ++i){
			x[0] += '#';
			x[h+1] += '#';
		}
		Grid a[h+2][w+2];
		for(int i = 0; i < h+2; ++i){
			for(int j = 0; j < w+2; ++j){
				if(x[i][j]=='#' || x[i][j]=='.') continue;
				if(x[i][j]=='W'){
					queue<pair<int,int> > q;
					q.push(make_pair(i,j));
					while(!q.empty()){
						int bh = q.front().first;
						int bw = q.front().second;
						q.pop();
						for(int dir = 0; dir < 4; ++dir){
							int nh = bh + dh[dir];
							int nw = bw + dw[dir];
							if(x[nh][nw]=='#' || x[nh][nw]=='B' || x[nh][nw]=='W') continue;
							if(a[nh][nw].w==1) continue;
							q.push(make_pair(nh,nw));
							a[nh][nw].w = 1;
						}
					}
				}else{
					queue<pair<int,int> > q;
					q.push(make_pair(i,j));
					while(!q.empty()){
						int bh = q.front().first;
						int bw = q.front().second;
						q.pop();
						for(int dir = 0; dir < 4; ++dir){
							int nh = bh + dh[dir];
							int nw = bw + dw[dir];
							if(x[nh][nw]=='#' || x[nh][nw]=='B' || x[nh][nw]=='W') continue;
							if(a[nh][nw].b==1) continue;
							q.push(make_pair(nh,nw));
							a[nh][nw].b = 1;
						}
					}
				}
			}
		}
		int ans_w = 0, ans_b = 0;
		for(int i = 1; i < h+1; ++i){
			for(int j = 1; j < w+1; ++j){
				if(a[i][j].w == 1 && a[i][j].b != 1){
					ans_w++;
				}
				if(a[i][j].w != 1 && a[i][j].b == 1){
					ans_b++;
				}
			}
		}
		// for(int i = 0; i < h+2; ++i){
		// 	for(int j = 0; j < w+2; ++j){
		// 		cout << a[i][j].b;
		// 	}
		// 	cout << endl;
		// }
		cout << ans_b << " " << ans_w << endl;

	}
	return 0;
}

#include <iostream>
#include <vector>
#include <algorithm>
#include <queue>
using namespace std;

typedef pair<int,int> P;

int w = 0, b = 0;
int n, m;
char d[50][50];
const int dx[] = {0,1,0,-1}, dy[] = {1,0,-1,0};

void bfs (int x, int y, char s, char t, char u) {
    queue<P> q;
    q.push(P(x,y));

    while (q.size()) {
        P p = q.front();
        q.pop();
        for (int i = 0; i < 4; i++) {
            int x = p.first + dx[i], y = p.second + dy[i];
            if (0 <= x && x < m && 0 <= y && y < n) {
                if (d[x][y] == '.') {
                    if (s == 'W') w++;
                    else b++;
                    q.push(P(x,y));
                    d[x][y] = t;
                } else if (d[x][y] == u) {
                    if (s == 'W') b--;
                    else w--;
                    q.push(P(x,y));
                    d[x][y] = t;
                }
            }
        }
    }
}

int main ()
{
    while (cin >> n >> m, (n || m)) {
        for (int i = 0; i < m; i++) {
            cin >> d[i];
        }

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (d[i][j] == 'W') {
                    bfs(i, j, 'W', '*', '@');
                }
            }
        }
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (d[i][j] == 'B') {
                    bfs(i, j, 'B', '@', '*');
                }
            }
        }
        cout << b << "/" << w << endl;
        w = b = 0;
    }

    return 0;
}

#include<iostream>
using namespace std;

int W,H;
char table[60][60] ;
char d[] = { '+' , '-' };
int dx[] = {-1,0,1,0};
int dy[] = {0,-1,0,1};

void search( int x ,int y ,int c ){

	if( table[y][x] == 'W' || table[y][x] == 'B' ) return ;
	if( table[y][x] == '*' || table[y][x] == d[c]) return ;

	if( table[y][x] == d[(c+1)%2] ){
		table[y][x] = '*' ;
	}else {
		table[y][x] = d[c] ;
	}

	for( int k=0 ; k<4 ; k++ ){
		search( x+dx[k] , y+dy[k] , c );
	}
}

int main(){

	while( cin >> W >> H ){
		if( !W && !H ) break ;

		for( int i=0 ; i<=H+1 ; i++ ){
			for( int j=0 ; j<=W+1 ; j++ ){
				if( !i || !j || i==H+1 || j==W+1 ) table[i][j] = '*' ;
				else cin >> table[i][j] ;
			}
		}

		for( int i=1 ; i<=H ; i++ ){
			for( int j=1 ; j<=W ; j++ ){
				if( table[i][j] == 'W' || table[i][j] == 'B' ){
					for( int k=0 ; k<4 ; k++ ){
						search( j+dx[k] , i+dy[k] , (table[i][j]=='W')?0:1 );
					}
				}
			}
		}

		int ansW=0,ansB=0;
		for( int i=1 ; i<=H ; i++ ){
			for( int j=1 ; j<=W ; j++ ){
				if( table[i][j] == '+' ) ansW++;
				if( table[i][j] == '-' ) ansB++;
			}
		}

		cout << ansB << " " ;
		cout << ansW << endl;
	}

	return 0;
}

#include <bits/stdc++.h>
using namespace std;

int dx[] = {1, 0, -1, 0};
int dy[] = {0, 1, 0, -1};

void dfs(vector<vector<char>>& v, int y, int x, bool w, bool b) {
    for (int i = 0; i < 4; i++) {
        int nx = x + dx[i];
        int ny = y + dy[i];
        if (v[ny][nx] == 'W') w = true;
        if (v[ny][nx] == 'B') b = true;
        if (!w & !b)
            v[y][x] = '0';
        else if (w & !b)
            v[y][x] = '1';
        else if (!w & b)
            v[y][x] = '2';
        else
            v[y][x] = '3';
        if (v[ny][nx] == '.') {
            dfs(v, ny, nx, w, b);
        }
    }
}

int solve(vector<vector<char>>& v, int y, int x, int& c) {
    c++;
    int m   = v[y][x] - '0';
    v[y][x] = '.';
    for (int i = 0; i < 4; i++) {
        int nx = x + dx[i];
        int ny = y + dy[i];
        if (v[ny][nx] == '0' || v[ny][nx] == '1' ||
            v[ny][nx] == '2' || v[ny][nx] == '3') {
            m = max(solve(v, ny, nx, c), m);
        }
    }
    return m;
}

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);

    while (1) {
        int w, h;
        cin >> w >> h;
        if (!w & !h) break;
        vector<vector<char>> field;
        field.push_back(vector<char>(w + 2, '#'));
        for (int i = 0; i < h; i++) {
            vector<char> res;
            string tmp;
            cin >> tmp;
            res.push_back('#');
            for (int j = 0; j < w; j++) {
                res.push_back(tmp[j]);
            }
            res.push_back('#');
            field.push_back(res);
        }
        field.push_back(vector<char>(w + 2, '#'));
        int wCount = 0, bCount = 0, tmp;
        for (int i = 1; i <= h; i++) {
            for (int j = 1; j <= w; j++) {
                if (field[i][j] == '.') {
                    dfs(field, i, j, false, false);
                }
            }
        }
        for (int i = 1; i <= h; i++) {
            for (int j = 1; j <= w; j++) {
                if (field[i][j] == '0' || field[i][j] == '1' ||
                    field[i][j] == '2' || field[i][j] == '3') {
                    int c = 0;
                    int m = solve(field, i, j, c);
                    if (m == 2) bCount += c;
                    if (m == 1) wCount += c;
                }
            }
        }
        cout << bCount << " " << wCount << endl;
    }
}

