        if(!h&&!w)break;
        vector<string> hw(h);
        rep(i,h)cin>>hw[i];

        ll px[]={0,1,0,-1};
        ll py[]={1,0,-1,0};
        ll ansb=0;
        ll answ=0;


        rep(i,h){
            rep(j,w){
                if(hw[i][j]=='W'||hw[i][j]=='B'){
                    char clo=hw[i][j];
                    rep(p,4){
                        bool f=true;
                        ll nx=j+px[p];
                        ll ny=i+py[p];
                        if(nx<0||nx>=w||ny<0||ny>=h||hw[ny][nx]!='.')continue;
                        queue<pair<ll,ll> > qxy;
                        qxy.push(make_pair(nx,ny));
                        hw[ny][nx]='N';
                        while(!qxy.empty()){
