                            ll nny=qxy.front().second;
                            qxy.pop();
                            rep(q,4){
                                ll tx=nnx+px[q];
                                ll ty=nny+py[q];
                                if(tx<0||tx>=w||ty<0||ty>=h)continue;
                                else if(hw[ty][tx]=='W'||hw[ty][tx]=='B'){
                                    if(hw[ty][tx]!=clo)f=false;
                                }else if(hw[ty][tx]=='.'){
                                    hw[ty][tx]='N';
                                    qxy.push(make_pair(tx,ty));
                                }
                            }
                        }
                        if(f){
                            ll tmp=1;
                            queue<pair<ll,ll> > qnp;
                            qnp.push(make_pair(nx,ny));
                            hw[ny][nx]='E';
                            while(!qnp.empty()){
                                ll nnx=qnp.front().first;
                                ll nny=qnp.front().second;
