                                    qnp.push(make_pair(tx,ty));
                                }
                            }
                            if(clo=='W')answ+=tmp;
                            else ansb+=tmp;
                        }
                    }
                }
            }
        }
        cout<<ansb<<" "<<answ<<endl;
    }
}

