      for(int j=0;j<w;j++){
	if(a[i][j]=='W'){
	  look(i,j,'W');
	}
	else if(a[i][j]=='B'){
	  look(i,j,'B');
	}
      }
    }
    cout << cb << " " << cw << endl;
  }
  return 0;

}
void look(int I,int J,char cl){
  int lx[4]={-1,0,1,0};
  int ly[4]={0,-1,0,1};
  for(int i=0;i<4;i++){
    if(I+lx[i]<0||I+lx[i]>=h||J+ly[i]<0||J+ly[i]>=w)continue;
    if(a[I+lx[i]][J+ly[i]]=='.'){
      cou=0;
      zen(I+lx[i],J+ly[i],cl);
      if(cou!=-1){
	if(cl=='B')cb+=cou;
	else cw+=cou;
      }
    }
  }
}
void zen(int I,int J,char cl){
  if(a[I][J]=='.'){
    a[I][J]='l';
    if(cou>=0)cou++;
  }
  else if(a[I][J]==cl||a[I][J]=='l')return;
  else{
    cou = -1;
    return;
  }
  int lx[4]={-1,0,1,0};
  int ly[4]={0,-1,0,1};
  for(int i=0;i<4;i++){
    if(I+lx[i]<0||I+lx[i]>=h||J+ly[i]<0||J+ly[i]>=w)continue;
    zen(I+lx[i],J+ly[i],cl);
  }
}

#include <iostream>
#include <string>
#include <vector>

using namespace std;

const int dx[] = { 0, 1, 0, -1 };
const int dy[] = { 1, 0, -1, 0 };

bool between(int a, int b, int c){
	return a <= b && b < c;
}

void paint(
	int x, int y, const vector<string> &field,
	vector< vector<int> > &canvas)
{
	const int W = field[0].size(), H = field.size();
	if(!between(0, x, W) || !between(0, y, H)){ return; }
	if(field[y][x] != '.' || canvas[y][x]){ return; }
	canvas[y][x] = 1;
	for(int i = 0; i < 4; ++i){
		paint(x + dx[i], y + dy[i], field, canvas);
	}
}

int main(){
	while(true){
		int w, h;
		cin >> w >> h;
		if(w == 0 && h == 0){ break; }
		vector<string> field(h);
		for(int i = 0; i < h; ++i){ cin >> field[i]; }
		vector< vector<int> > white(h, vector<int>(w));
		vector< vector<int> > black(h, vector<int>(w));
		for(int y = 0; y < h; ++y){
			for(int x = 0; x < w; ++x){
				for(int i = 0; i < 4; ++i){
					if(field[y][x] == 'W'){
						paint(x + dx[i], y + dy[i], field, white);
					}else if(field[y][x] == 'B'){
						paint(x + dx[i], y + dy[i], field, black);
					}
				}
			}
		}
		int wsum = 0, bsum = 0;
		for(int y = 0; y < h; ++y){
			for(int x = 0; x < w; ++x){
				if(white[y][x] == black[y][x]){ continue; }
				if(white[y][x]){ ++wsum; }
				if(black[y][x]){ ++bsum; }
			}
		}
		cout << bsum << " " << wsum << endl;
	}
	return 0;
}

#include <bits/stdc++.h>
using namespace std;

int dx[] = {1, 0, -1, 0};
int dy[] = {0, 1, 0, -1};

void dfs(vector<vector<char>>& v, int y, int x, bool w, bool b) {
    for (int i = 0; i < 4; i++) {
        int nx = x + dx[i];
        int ny = y + dy[i];
        if (v[ny][nx] == 'W') w = true;
        if (v[ny][nx] == 'B') b = true;
        if (!w & !b)
            v[y][x] = '0';
        else if (w & !b)
            v[y][x] = '1';
        else if (!w & b)
            v[y][x] = '2';
        else if (w & b)
            v[y][x] = '3';
    }
    for (int i = 0; i < 4; i++) {
        int nx = x + dx[i];
        int ny = y + dy[i];
        if (v[ny][nx] == '.') {
            dfs(v, ny, nx, w, b);
        }
    }
}

int solve(vector<vector<char>>& v, int y, int x, int& c) {
    c++;
    int m   = v[y][x] - '0';
    v[y][x] = '.';
    for (int i = 0; i < 4; i++) {
        int nx = x + dx[i];
        int ny = y + dy[i];
        if (v[ny][nx] == '0' || v[ny][nx] == '1' ||
            v[ny][nx] == '2' || v[ny][nx] == '3') {
            m = max(solve(v, ny, nx, c), m);
        }
    }
    return m;
}

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);

    while (1) {
        int w, h;
        cin >> w >> h;
        if (!w & !h) break;
        vector<vector<char>> field;
        field.push_back(vector<char>(w + 2, '#'));
        for (int i = 0; i < h; i++) {
            vector<char> res;
            string tmp;
            cin >> tmp;
            res.push_back('#');
            for (int j = 0; j < w; j++) {
                res.push_back(tmp[j]);
            }
            res.push_back('#');
            field.push_back(res);
        }
        field.push_back(vector<char>(w + 2, '#'));
        int wCount = 0, bCount = 0, tmp;
        for (int i = 1; i <= h; i++) {
            for (int j = 1; j <= w; j++) {
                if (field[i][j] == '.') {
                    dfs(field, i, j, false, false);
                }
            }
        }
        for (int i = 1; i <= h; i++) {
            for (int j = 1; j <= w; j++) {
                if (field[i][j] == '0' || field[i][j] == '1' ||
                    field[i][j] == '2' || field[i][j] == '3') {
                    int c = 0;
                    int m = solve(field, i, j, c);
                    if (m == 2) bCount += c;
                    if (m == 1) wCount += c;
                }
            }
        }
        cout << bCount << " " << wCount << endl;
    }
}

