using namespace std;

char map[50][51];
int w,h,table[50][50],d_row[4] = {-1,0,0,1},d_col[4] = {0,-1,1,0};

bool rangeCheck(int row,int col){
	if(row >= 0 && row <= h-1 && col >= 0 && col <= w-1)return true;
	else{
		return false;
	}
}

void paint(int row,int col){
	if(table[row][col] == 2 || table[row][col] == 1){
		for(int i = 0; i < 4; i++){
			if((rangeCheck(row+d_row[i],col+d_col[i]) == true) && (table[row+d_row[i]][col+d_col[i]] == 0 || table[row+d_row[i]][col+d_col[i]] == 10)){
				table[row+d_row[i]][col+d_col[i]] += 1;
				paint(row+d_row[i],col+d_col[i]);
			}
		}

	}else if(table[row][col] == 4 || table[row][col] == 10){
		for(int i = 0; i < 4; i++){
			if((rangeCheck(row+d_row[i],col+d_col[i]) == true) && (table[row+d_row[i]][col+d_col[i]] == 0 || table[row+d_row[i]][col+d_col[i]] == 1)){
				table[row+d_row[i]][col+d_col[i]] += 10;
				paint(row+d_row[i],col+d_col[i]);
			}
		}
	}else if(table[row][col] == 11){
		for(int i = 0; i < 4; i++){
			if((rangeCheck(row+d_row[i],col+d_col[i]) == true) && (table[row+d_row[i]][col+d_col[i]] == 0 || table[row+d_row[i]][col+d_col[i]] == 1 || table[row+d_row[i]][col+d_col[i]] == 10)){
				table[row+d_row[i]][col+d_col[i]] = 11;
				paint(row+d_row[i],col+d_col[i]);
			}
		}
	}

}


void func(){

	for(int i = 0; i < h; i++){
		for(int k = 0; k < w; k++)table[i][k] = 0;
	}

	for(int i = 0; i < h; i++){
		scanf("%s",map[i]);
	}

	for(int i = 0; i < h; i++){
		for(int k = 0; k < w; k++){
			if(map[i][k] == 'B'){
				table[i][k] = 2;
			}else if(map[i][k] == 'W'){
				table[i][k] = 4;
			}
		}
	}

	for(int i = 0; i < h; i++){
		for(int k = 0; k < w; k++){
			if(table[i][k] == 2 || table[i][k] == 4){
				paint(i,k);
			}
		}
	}

	int black = 0,white = 0;

	for(int i = 0; i < h; i++){
		for(int k = 0; k < w; k++){
			if(table[i][k] == 1){
				black++;
			}else if(table[i][k] == 10){
				white++;
			}

		}
	}

	printf("%d %d\n",black,white);

}


int main(){

	while(true){
		scanf("%d %d",&w,&h);
		if(w == 0 && h == 0)break;

		func();
	}

	return 0;
}

#include <cstdio>
#include <cstring>
#include <algorithm>
using namespace std;
char m[55][55];
bool used[55][55];
int w,h;
int dx[] = {0,0,1,-1};
int dy[] = {1,-1,0,0};
char c,c_;
bool f;

int solve(int x,int y){
  int ret = 1;
  used[x][y] = false;
  for(int i = 0; i < 4; i++){
    int nx = x + dx[i];
    int ny = y + dy[i];
    if(m[nx][ny] == c){
      f = true;
    }
    if(m[nx][ny] == c_){
      return -1000000;
    }
    if(m[nx][ny] == '.' && used[nx][ny]){
      ret += solve(nx,ny);
    }
  }
  return ret;
}

int main(void){
  while(1){
    scanf("%d%d",&w,&h);
    if(!w && !h) break;
    memset(m,0,sizeof(m));
    for(int i = 1; i <= h; i++){
      for(int j = 1; j <= w; j++){
	scanf(" %c",&m[i][j]);
      }
    }
    for(int k = 0; k < 2; k++){
      int ans = 0;
      memset(used,true,sizeof(used));
      c_ = (k)?'B':'W';
      c = (!k)?'B':'W';
      for(int i = 1; i <= h; i++){
	for(int j = 1; j <= w; j++){
	  if(m[i][j] == '.' && used[i][j]){
	    f = false;
	    int ret = max(0,solve(i,j));
	    if(f){
	      ans += ret;
	    }
	  }
	}
      }
      printf("%d%c",ans,(k)?'\n':' ');
    }
  }
}

