const int dir[][2] = {
	1, 0,
	0, 1,
	-1, 0,
	0, -1
};

void dfs( VS &board, set<char> &chars, int &count, int y, int x )
{
	if ( !( 0 <= y && y < board.size() && 0 <= x && x < board[0].size() ) )
	{
		return;
	}

	if ( board[y][x] == '#' )
	{
		return;
	}

	if ( board[y][x] != '.' )
	{
		chars.insert( board[y][x] );
		return;
	}

	board[y][x] = '#';
	count++;

	REP( d, 0, 4 )
	{
		dfs( board, chars, count, y + dir[d][0], x + dir[d][1] );
	}

	return;
}

int main()
{
	cin.tie( 0 );
	ios::sync_with_stdio( false );

	while ( true )
	{
		int w, h;
		cin >> w >> h;

		if ( !( w | h ) )
		{
			break;
		}

		VS board( h );

		EACH( line, board )
		{
			cin >> line;
		}

		map<char,int> res;

		REP( i, 0, h )
		{
			REP( j, 0, w )
			{
				set<char> chars;
				int count = 0;

				dfs( board, chars, count, i, j );

				if ( chars.size() == 1 )
				{
					res[ *chars.begin() ] += count;
				}
			}
		}

		cout << res['B'] << ' ' << res['W'] << endl;
	}

	return 0;
}

