int used[60][60],dx[]={1,0,-1,0},dy[]={0,1,0,-1};

pii dfs(vector<string> &mp,int y,int x){
  if(mp[y][x]=='#')
    return pii(0,0);
  if(mp[y][x]=='B')
    return pii(0,2);
  if(mp[y][x]=='W')
    return pii(0,1);
  if(used[y][x])
    return pii(0,0);
  used[y][x]=1;
  pii re=pii(1,0),tmp;
  rep(i,4){
    tmp=dfs(mp,y+dy[i],x+dx[i]);
    re.X+=tmp.X;
    re.Y|=tmp.Y;
  }
  return re;
}

