void white(int i,int j){
	if(a[i-1][j] == 'B' && i-1 >= 0){
		a[i][j] = 'n';
		return;
	}
	else if(a[i+1][j] == 'B' && i+1 < h){
		a[i][j] = 'n';
		return;
	}
	else if(a[i][j-1] == 'B' && j-1 >= 0){
		a[i][j] = 'n';
		return;
	}
	else if(a[i][j+1] == 'B' && j+1 < w){
		a[i][j] = 'n';
		return;
	}
	if(a[i-1][j] == '.' && i-1 >= 0){
		a[i-1][j] = 'w';
		white(i-1,j);
	}
	if(a[i+1][j] == '.' && i+1 < h){
		a[i+1][j] = 'w';
		white(i+1,j);
	}
	if(a[i][j-1] == '.' && j-1 >= 0){
		a[i][j-1] = 'w';
		white(i,j-1);
	}
	if(a[i][j+1] == '.' && j+1 < w){
