	if( color == 1 && map[color][xpos][ypos] == 'B' ) return;
	if( map[color][xpos][ypos] == '#' ) return;
	map[color][xpos][ypos] = '#';
	search_dfs( map, xpos + 1, ypos, w, h, color);
	search_dfs( map, xpos - 1, ypos, w, h, color);
	search_dfs( map, xpos, ypos - 1, w, h, color);
	search_dfs( map, xpos, ypos + 1, w, h, color);

}


