	while ( w != 0 || h != 0 ) {
		vector< vector<int> > area(h+2, vector<int>(w+2));
		queue<int> checkx,checky;

		for (int i = 0; i < h+2; i++) {
			for (int j = 0; j < w+2; j++) {
				if (i==0||i==h+1||j==0||j==w+1) {
					area[i][j] = -1;
				}
				else {
					cin >> c;
					if ( c == 'W' ) {
						area[i][j] = 1;
					}
					else if ( c == 'B' ) {
						area[i][j] = 2;
					}
					else {
						area[i][j] = 0;
						checky.push(i);
						checkx.push(j);
					}
				}
			}
		}

		int count_b = 0;
		int count_w = 0;
		int count = 0;
		int mode = 0;
		int xn,yn;
		queue<int> sx,sy;
		while (!checkx.empty()) {
			xn = checkx.front();
			yn = checky.front();
			checkx.pop();
			checky.pop();
			count = 0;
			mode = 0;
			if (area[yn][xn] == 0) {
				count++;
				sx.push(xn);
				sy.push(yn);
				while (!sx.empty()) {
					xn = sx.front();
					yn = sy.front();
					area[yn][xn] = -1;
					sx.pop();
					sy.pop();
					if (area[yn+1][xn] == 0) {
						sx.push(xn);
						sy.push(yn+1);
						area[yn+1][xn] = -1;
						count++;
					}
					if (area[yn-1][xn] == 0) {
						sx.push(xn);
						sy.push(yn-1);
						area[yn-1][xn] = -1;
						count++;
					}
					if (area[yn][xn+1] == 0) {
						sx.push(xn+1);
						sy.push(yn);
						area[yn][xn+1] = -1;
						count++;
					}
					if (area[yn][xn-1] == 0) {
						sx.push(xn-1);
						sy.push(yn);
						area[yn][xn-1] = -1;
						count++;
					}
					if (area[yn+1][xn] > 0) {
						if (mode == 0) {
							mode = area[yn+1][xn];
						}
						else if (mode != area[yn+1][xn]) {
							mode = -1;
						}
					}
					if (area[yn-1][xn] > 0) {
						if (mode == 0) {
							mode = area[yn-1][xn];
						}
						else if (mode != area[yn-1][xn]) {
							mode = -1;
						}
					}
					if (area[yn][xn+1] > 0) {
						if (mode == 0) {
							mode = area[yn][xn+1];
						}
						else if (mode != area[yn][xn+1]) {
							mode = -1;
						}
					}
					if (area[yn][xn-1] > 0) {
						if (mode == 0) {
							mode = area[yn][xn-1];
						}
						else if (mode != area[yn][xn-1]) {
							mode = -1;
						}
					}
				}
				if (mode > 0) {
					if (mode == 1) {
						count_w += count;
					}
					else {
						count_b += count;
					}
				}
			}
		}
		cout << count_b << " " << count_w << endl;

		cin >> w >> h;
	}

	return 0;
}

