const vector<int> dx = { 0, 1, 0, -1 };
const vector<int> dy = { -1, 0, 1, 0 };

int rec(int x, int y, vector<vector<bool> > ok)
{
	if (M[y][x] == 'W')
	{
		return 2;
	}

	if (M[y][x] == 'B')
	{
		return 1;
	}

	int ret = 3;

	for (int dir = 0; dir < 4; dir++)
	{
		int tx = x + dx[dir];
		int ty = y + dy[dir];

		if (0 <= tx && tx < W && 0 <= ty && ty < H && ok[ty][tx] == false)
		{
			vector<vector<bool> > ok2 = ok; ok2[ty][tx] = true;

			ret = ret & rec(tx, ty, ok2);
		}
	}

	return ret;
}

int main()
{
