	while (true)
	{
		cin >> W >> H;

		if (W == 0 && H == 0) { break; }

		int b = 0, w = 0;

		for (int i = 0; i < H; i++)
		{
			for (int j = 0; j < W; j++)
			{
				int c = rec(j, i, vector<vector<bool> >(H, vector<bool>(W, false)));

				b += c & 1;
				w += (c & 2) / 2;
			}
		}

		cout << b << ' ' << w << endl;
	}

	return 0;
}

