int main(void) {
    while (1) {
        cin >> w >> h;
        if (w == 0 && h == 0) break;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                cin >> a[i][j];
            }
        }
        int white = 0, black = 0;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
