using namespace std;
struct data
{
	int x;
	int y;
};
struct mapData
{
	int black = 0;
	int white = 0;
};
int main()
{
	int w, h;
	int dx[4] = {1,0,-1,0};
	int dy[4] = {0,1,0,-1};
	while (cin >> w >> h && w != 0 || h != 0)
	{
		char map[50][50];
		for (int i = 0; i < h; i++)
		{
			cin >> map[i];
		}

		mapData whiteBlack[50][50];
		for (int i = 0; i < h; i++)
		{
			for (int j = 0; j < w; j++)
			{
				whiteBlack[i][j].black = 0;
				whiteBlack[i][j].white = 0;
			}
		}

		for (int i = 0; i < h; i++)
		{
			for (int j = 0; j < w; j++)
			{
				if (map[i][j] == '.')continue;
				int color = (map[i][j] == 'B' ? 1 : 2);
				queue<data> q;
				data first;
				first.x = j;
				first.y = i;
				q.push(first);
				while (q.size() > 0)
				{
					data d = q.front(); q.pop();
					if (map[d.y][d.x] == '.')
					{
						if (color == 1)
						{
							whiteBlack[d.y][d.x].black = 1;
						}
						else
						{
							whiteBlack[d.y][d.x].white = 1;
						}
					}
					for (int k = 0; k < 4; k++)
					{
						int nx = dx[k] + d.x;
						int ny = dy[k] + d.y;
						if (nx >= w || ny >= h || nx < 0 || ny < 0)continue;
						if (map[ny][nx] == 'W' || map[ny][nx] == 'B')continue;
						if (color == 1 && whiteBlack[ny][nx].black == 1)continue;
						if (color == 2 && whiteBlack[ny][nx].white == 1)continue;
						data add;
						add.x = nx;
						add.y = ny;
						q.push(add);
					}
				}
			}
		}
		int white = 0;
		int black = 0;
		for (int i = 0; i < h; i++)
		{
			for (int j = 0; j < w; j++)
			{
				if (whiteBlack[i][j].black == 1 && whiteBlack[i][j].white == 0)
				{
					black++;
				}
				else if (whiteBlack[i][j].white == 1 && whiteBlack[i][j].black == 0)
				{
					white++;
				}
			}
		}

		cout << black << " " << white << endl;
	}
	return 0;
}

#include <bits/stdc++.h>
#define rep(i,n) for(int i=0;i<(n);i++)
using namespace std;
typedef pair <int,int> pii;
#define FST first
#define SEC second

int w,h;
int dd[8]={0,1,0,-1,0};

void bfs(char map[50][50]){
  int bc=0,wc=0;
  rep(i,h){
    rep(j,w){
      if(map[j][i]=='.'){
	queue <pii> q;
	q.push(pii(j,i));
	int count=0;
	pii state=pii(0,0);
	while(!q.empty()){
	  int cx=q.front().FST,cy=q.front().SEC;
	  q.pop();
	  if(map[cx][cy]=='.')
	    count++;
	  map[cx][cy]='#';
	  rep(r,4){
	    int nx=cx+dd[r],ny=cy+dd[r+1];
	    if(nx<0 || ny<0 || nx>=w || ny>=h || map[nx][ny]=='#')
	      continue;
	    if(map[nx][ny]=='B'){
	      state.FST=1;
	      continue;
	    }
	    if(map[nx][ny]=='W'){
	      state.SEC=1;
	      continue;
	    }
	    q.push(pii(nx,ny));
	  }
	}
	if(state==pii(1,0))
	  bc+=count;
	if(state==pii(0,1))
	  wc+=count;
      } 
    }
  }
  printf("%d %d\n",bc,wc);
}

int main(){
  for(;;){
    cin >> w >> h;
    if(!(w+h)) break;
    char map[50][50];
    rep(i,h){
      rep(j,w){
        cin >> map[j][i];
      }
    }
    bfs(map);
  }
}

#include<cstdio>
#include<iostream>
#include<cstring>

#define W_MAX 50
#define H_MAX 50


using namespace std;

int map[H_MAX][W_MAX];
int zyo[H_MAX][W_MAX];
int w,h;
int flag;

void solve(int H,int W){
	int dx[4]={-1,1,0, 0};
	int dy[4]={ 0,0,1,-1};
	/*cout<<H<<","<<W<<","<<map[H][W]<<","<<zyo[H][W]<<","<<flag<<endl;*/
	for(int a=0;a<4;a++){
			if(H+dx[a]<0||H+dx[a]>=h) continue;
			if(W+dy[a]<0||W+dy[a]>=w) continue;
			if(zyo[H+dx[a]][W+dy[a]]==-1&&map[H+dx[a]][W+dy[a]]==0){
				zyo[H+dx[a]][W+dy[a]]=3;
				solve(H+dx[a],W+dy[a]);
			}
			else if(map[H+dx[a]][W+dy[a]]==1){
				if(flag==-1) flag=1;
				if(flag==2) flag=0;
			}
			else if(map[H+dx[a]][W+dy[a]]==2){
				if(flag==-1) flag=2;
				if(flag==1) flag=0;
		}
	}
	return;
}

int main(void){

	char tmp;
	int bc,wc;

	while(1){
			memset(map,-1,sizeof(map));
			memset(zyo,-1,sizeof(zyo));
			bc=0,wc=0;

			cin>>w>>h;
			if(w==0&&h==0) break;
			for(int i=0;i<h;i++){
				for(int j=0;j<w;j++){
					cin>>tmp;
					switch(tmp){
					case '.':
						map[i][j]=0;
						zyo[i][j]=-1;
						break;
					case 'B':
						map[i][j]=1;
						zyo[i][j]=0;
						break;
					case 'W':
						map[i][j]=2;
						zyo[i][j]=0;
						break;
					}
				}
			}

		for(int i=0;i<h;i++){
			for(int j=0;j<w;j++){
				if(zyo[i][j]==-1){
					zyo[i][j]=3;
					flag=-1;
					solve(i,j);
					for(int k=0;k<h;k++){
						for(int l=0;l<w;l++){
							if(zyo[k][l]==3) zyo[k][l]=flag;
						}
					}
				}
			}
		}

		for(int i=0;i<h;i++){
			for(int j=0;j<w;j++){
				//cout<<zyo[i][j];
				if(zyo[i][j]==1) bc++;
				else if(zyo[i][j]==2) wc++;
			}
			//cout<<endl;
		}
		cout<<bc<<" "<<wc<<endl;
	}
	return 0;
}

