void renew(int x,int y){
  int dx[4]={1,0,-1,0},dy[4]={0,1,0,-1},newx,newy;
  for(int u=0;u<4;u++){
    newx=x+dx[u],newy=y+dy[u];
    if(newx>=0 && newx<w && newy>=0 && newy<h){
         if(clr[newx][newy]=='.' && map[newx][newy]=='.') map[newx][newy]=map[x][y],renew(newx,newy);
      else if(clr[newx][newy]=='.' && map[x][y]!=map[newx][newy])map[newx][newy]='F',renew(newx,newy);
    }
  }
}

