#include <iostream>
#include <vector>

using namespace std;

#define INF 1e8

int W, H;
vector<string> m;
int check(int x, int y, char c) {
	int ret = 0;
	if (x < 0 || x >= W || y < 0 || y >= H) return 0;
	switch (m[y][x]) {
		case '.':
			m[y][x] = '#';
			for (int dx = -1; dx <= 1; ++dx) {
				for (int dy = -1; dy <= 1; ++dy) {
					if (dx && dy) continue;

					ret += check(x+dx, y+dy, c);
				}
			}
			return ret < 0 ? -INF : ret;

		case '#':
			return 0;

		case 'B':
		case 'W':
			if (c != m[y][x]) return -INF;
			else return 1;
	}
	return true;
}
void fill(int x, int y, char from, char to) {
	if (x < 0 || x >= W || y < 0 || y >= H) return;
	if (m[y][x] == from) {
		m[y][x] = to;
		for (int dx = -1; dx <= 1; ++dx) {
			for (int dy = -1; dy <= 1; ++dy) {
				if (dx && dy) continue;
				fill(x+dx, y+dy, from, to);
			}
		}
	}
	return;
}
int paint(int x, int y) {
	if (x < 0 || x >= W || y < 0 || y >= H) return 0;
	switch (m[y][x]) {
		case '.':
		{
			int ret = 1;
			m[y][x] = '#';
			for (int dx = -1; dx <= 1; ++dx) {
				for (int dy = -1; dy <= 1; ++dy) {
					if (dx && dy) continue;
					ret += paint(x+dx, y+dy);
				}
			}
			return ret;
		}
		case '#':
		case 'B':
		case 'W':
			return 0;
	}
	return 0;
}

int main() {
	while ( cin >> W >> H, W || H ) {
		m.clear(); m.resize(H);
		for (int y = 0; y < H; ++y) {
			cin >> m[y];
		}
		int aw = 0, ab = 0;
		for (int y = 0; y < H; ++y) {
			for (int x = 0; x < W; ++x) {
				if (m[y][x] == '.') {
					if ( check(x, y, 'B') >= 1 ) {
						fill(x, y, '#', '.');
						ab += paint(x, y);
					}
					fill(x, y, '#', '.');
					if ( check(x, y, 'W') >= 1 ) {
						fill(x, y, '#', '.');
						aw += paint(x, y);
					}
				}
			}
		}
		cout << ab << " " << aw << endl;
	}
}

#include<iostream>
