int w, h;

int dx[4] = {1,0,-1,0};
int dy[4] = {0,1,0,-1};

bool B[50][50], W[50][50];
char field[50][50];

void dfs(int y, int x, bool color){
	for(int k=0; k<4; k++){
		if(x+dx[k]<0 || x+dx[k]>=w || y+dy[k]<0 || y+dy[k]>=h) continue;
		if(field[y+dy[k]][x+dx[k]]=='.'){
			if(color){
				if(!W[y+dy[k]][x+dx[k]]){
					W[y+dy[k]][x+dx[k]] = true;
					dfs(y+dy[k],x+dx[k],1);
				}
			}else{
				if(!B[y+dy[k]][x+dx[k]]){
					B[y+dy[k]][x+dx[k]] = true;
					dfs(y+dy[k],x+dx[k],0);
				}
			}
		}
	}
}

