		for(int i=0; i<h; i++){
			for(int j=0; j<w; j++){
				cin >> field[i][j];
				B[i][j] = false;
				W[i][j] = false;
			}
		}
		for(int i=0; i<h; i++){
			for(int j=0; j<w; j++){
				if(field[i][j] == 'B') dfs(i,j,0);
				if(field[i][j] == 'W') dfs(i,j,1);
			}
		}
		for(int i=0; i<h; i++){
			for(int j=0; j<w; j++){
				if( B[i][j] && !W[i][j]) b_count++;
				if(!B[i][j] &&  W[i][j]) w_count++;
			}
		}
		cout << b_count << " " << w_count << endl;
	}
    return 0;
}

