bool used[51][51];
char field[51][51];

using namespace std;

const int dy[]={-1,0,0,1};
const int dx[]={0,1,-1,0};

int h,w;
bool wok,bok;
int bfs(int x,int y){

	queue<pair<int,int> > *prv=new queue<pair<int,int> >();
	queue<pair<int,int> > *nxt=new queue<pair<int,int> >();

	bool white=false;
	bool black=false;

	int cnt=0;
	prv->push(make_pair(y,x));
	while(prv->size()){
		while(prv->size()){
			pair<int,int> cp = prv->front();prv->pop();
			if(used[cp.first][cp.second]){
				continue;
			}
			used[cp.first][cp.second]=true;
			cnt++;
			for(int i = 0; i < 4; i++){
				pair<int,int> np=cp;
				np.first+=dy[i];
				np.second+=dx[i];
				if(np.first >= 0 && np.second >= 0 && np.first < h && np.second < w){
					if(field[np.first][np.second]=='W'){
						white=true;
						continue;
					}
					else if(field[np.first][np.second]=='B'){
						black=true;
						continue;
					}

					if(!used[np.first][np.second]){
						nxt->push(np);
					}
				}
			}
		}
		swap(prv,nxt);
	}
	delete prv,nxt;

	if(white && black)
		return 0;
	else if(white){
		wok=true;
		return cnt;
	}
	else if(black){
		bok=true;
		return cnt;
	}
	else
		return 0;
}



int main(){

	while(cin>>w>>h&&!(w==0&&h==0)){
		for(int i = 0; i < h; i++)
			fill(used[i],used[i]+w,false);

		int wcnt=0;
		int bcnt=0;
		for(int i = 0; i < h; i++){
			for(int j = 0; j < w; j++){
				cin>>field[i][j];
			}
		}
		for(int i = 0; i < h; i++){
			for(int j = 0; j < w; j++){
				if(i==0&&j==4){
//					cout<<endl;
				}
				wok=false;
				bok=false;
				if(used[i][j]||field[i][j]!='.')
					continue;
				int ccc = bfs(j,i);
				if(wok)
					wcnt+=ccc;
				else if(bok)
					bcnt+=ccc;
			}
		}
		cout << bcnt << " " << wcnt << endl;
	}

	return 0;
}

