int w,h;
char map[70][70], wmap[70][70], bmap[70][70];

void wdfs(int x, int y) {
    if(x<0 || x>=w || y<0 || y>=h) return;
    if(map[y][x] != '.') return;
    if(wmap[y][x]) return;
    wmap[y][x] = 1;
    wdfs(x-1,y);
    wdfs(x,y-1);
    wdfs(x+1,y);
    wdfs(x,y+1);
}
void bdfs(int x, int y) {
    if(x<0 || x>=w || y<0 || y>=h) return;
    if(map[y][x] != '.') return;
    if(bmap[y][x]) return;
    bmap[y][x] = 1;
    bdfs(x-1,y);
    bdfs(x,y-1);
    bdfs(x+1,y);
    bdfs(x,y+1);
}

int main(void) {
    while(true) {
        scanf("%d%d",&w,&h);

        if(!w) break;
        memset(map, 0, sizeof(map));
        memset(wmap, 0, sizeof(wmap));
        memset(bmap, 0, sizeof(bmap));

        for(int j=0; j<h; j++)
            scanf("%s", map[j]);
        for(int j=0; j<h; j++)
            for(int i=0; i<w; i++)
                switch(map[j][i]) {
                    case 'W':
                        wdfs(i-1,j);
                        wdfs(i,j-1);
                        wdfs(i+1,j);
                        wdfs(i,j+1);
                        break;
                    case 'B':
                        bdfs(i-1,j);
                        bdfs(i,j-1);
                        bdfs(i+1,j);
                        bdfs(i,j+1);
                        break;
                }
        int bres=0,wres=0;
        for(int j=0; j<h; j++)
            for(int i=0; i<w; i++)
                if(wmap[j][i] && !bmap[j][i]) wres++;
                else if(bmap[j][i] && !wmap[j][i]) bres++;

        printf("%d %d\n", bres, wres);

    }
    return 0;
}

