int a[51][51];
int color[51][51];
bool checked[51][51];
int w, h;

int dfs(int y, int x) {
  if (y < 0 || y >= h) {
    return 0b100;
  }
  if (x < 0 || x >= w) {
    return 0b100;
  }
  if (a[y][x] == 1) {
    return 0b001;
  } else if (a[y][x] == 2) {
    return 0b010;
  }
  if (checked[y][x]) {
    return 0;
  }

  int dy[] = { -1, 0, 1, 0 };
  int dx[] = { 0, 1, 0, -1 };

  checked[y][x] = true;
  int ret = 0;
  for (int i = 0; i < 4; i++) {
    ret |= dfs(y + dy[i], x + dx[i]);
  }

  return ret;
}

int main() {
  while (true) {
    for (int i = 0; i < 51; i++) {
      for (int j = 0; j < 51; j++) {
        a[i][j] = 0;
        color[i][j] = 0;
        checked[i][j] = false;
      }
    }

    cin >> w >> h;
    if (w + h == 0) {
      break;
    }
    for (int i = 0; i < h; i++) {
      string str;
      cin >> str;
      for (int j = 0; j < w; j++) {
        if (str[j] == '.') {
          a[i][j] = 0;
        } else if (str[j] == 'B') {
          a[i][j] = 1;
        } else {
          a[i][j] = 2;
        }
      }
    }
    int black = 0, white = 0;
    for (int i = 0; i < h; i++) {
      for (int j = 0; j < w; j++) {
        if (a[i][j] != 0) {
          continue;
        }
        for (int k = 0; k < h; k++) {
          for (int l = 0; l < w; l++) {
            checked[k][l] = false;
          }
        }
        int d = dfs(i, j);
        if (d == 0b001 || d == 0b101) {
          black++;
        }
        if (d == 0b010 || d == 0b110) {
          white++;
        }
      }
    }
    cout << black << " " << white << endl;

  }
  return 0;
}

