                        for(int i=0; i < 4; i++){
                            int nx = p.first+dx[i], ny = p.second+dy[i];
                            if(!isIn(nx, ny) || is[nx][ny]) continue;
                            if(field[nx][ny] == '.') q.push(make_pair(nx, ny));
                        }
                    }
                    if(r == 1){a+=count; field[i][j] = 'W';}
                    else if(r == 2){b+=count; field[i][j] = 'B';}
                }
            }
        }
        cout << b << " " << a << endl;
    }
}

