#define REP(i,n) for(int i=0;i<(n);i++)

int di[4] = {1, 0, -1, 0};
int dj[4] = {0, 1, 0, -1};
using namespace std;

typedef long long ll;
typedef pair<int, int> P;
const ll INF = 1e18;

int w, h;
char field[50][50];
bool visited[50][50];
bool white_flg;
bool black_flg;

int dfs(int i, int j){
    if(i<0 || h<=i || j<0 || j>=w || visited[i][j]) return 0;
    if(field[i][j] == 'B'){
        black_flg = true;
        return 0;
    }
    if(field[i][j] == 'W'){
        white_flg = true;
        return 0;
    }
    visited[i][j] = true;
    int res = 1;
    REP(k,4){
        res += dfs(i+di[k], j+dj[k]);
    }
    return res;
}

